# ERROR HANDLING API

@app.errorhandler(404)
def page_not_found(e):
    if 'loggedin' in session:
        return render_template("Error/New404.html", username=session['username'], userid=session['id']), 404
    else:
        return render_template('ASPX/ASP404.html'), 404

@app.errorhandler(500)
def interal_server_error(e):
    if 'loggedin' in session:
        return render_template("Error/New404.html", username=session['username'], userid=session['id']), 500
    else:
        return render_template('ASPX/ASP404.html'), 500

