# GET USER FRIENDS
@app.route('/api/v1/friends/v1/users/<userid>/friends')
def friends_api(userid):
    cursor = mysql.connection.cursor(MySQLdb.cursors.DictCursor)
    cursor.execute('SELECT * FROM accounts WHERE id = %s', (userid,))
    # Fetch one record and return result
    account = cursor.fetchone()
    
    if not account:
        return '[]'
    else:
        friendsdatasql = mysql.connection.cursor(MySQLdb.cursors.DictCursor)
        friendsdatasql.execute('SELECT * FROM freinds WHERE author = %s', (userid,))
        friendsdata = friendsdatasql.fetchall()
        
        jsoninitial = '{"data":['
        jsonend = ']}'
        
        mainjson = jsoninitial
        
        anyfriends = 0
        
        for friend in friendsdata:
        
            anyfriends = 1
        
            getfriendinfosql = mysql.connection.cursor(MySQLdb.cursors.DictCursor)
            getfriendinfosql.execute('SELECT * FROM accounts WHERE id = %s', (friend['friend'],))
            friendinfo = getfriendinfosql.fetchone()
        
        
            jsonfriendstring = '{"isOnline":false,"presenceType":0,"isDeleted":false,"friendFrequentScore":0,"friendFrequentRank":1,"description":null,"created":"0001-01-01T06:00:00Z","isBanned":false,"externalAppDisplayName":null,"id":' + str(friendinfo['id']) + ',"name":"' + friendinfo['username'] + '","displayName":"' + friendinfo['username'] + '"},'
        
            mainjson = mainjson + jsonfriendstring
        
        if anyfriends == 1:
            mainjson = mainjson[:-1]
        
        mainjson = mainjson + jsonend
    
    return mainjson

