# SIGNUP USERNAME VALIDATION
@app.route('/api/v1/auth/v1/usernames/validate', methods = ['POST'])
def signup_username_validate(): 
    parsedata = json.loads(request.data)
    

    cursor = mysql.connection.cursor(MySQLdb.cursors.DictCursor)
    cursor.execute('SELECT * FROM accounts WHERE username = %s', (parsedata["username"],))
    # Fetch one record and return result
    account = cursor.fetchone()
    
    if account:
        return '{"code":1,"message":"Username is already in use"}'
    else:
        return '{"code":0,"message":"Username is valid"}'

